import axios from 'axios';
import React from 'react';

class PersonasForm extends React.Component{

    constructor(props){
        super(props)
        this.state={}
    }
    
    //Funcion para enviar request POST al endpoint
    submitNuevaPersona(e){
        //Anular comportamiento por defecto del form
        e.preventDefault();
        //peticion con axios
        axios.post('http://localhost:3023/contactos', {
            nombre: this.refs.nombre.value,
            apellido: this.refs.apellido.value,
            email: this.refs.email.value,
            telefono: this.refs.telefono.value,
            empresa: this.refs.empresa.value
        }).then(res=>console.log(res.data)).catch(err=>console.log(err));
    }

    render(){
        return (
            <div className="container">
            <div className="row">
                <div className="col s4">  
                <a className="waves-effect waves-light btn modal-trigger" href="#modal1"><i className='bx bx-plus'></i> Nuevo Usuario</a>
                </div>
            </div>
            <div id="modal1" className="modal">
            <div className="modal-content">
               <div className="row">
                   <h4 className="center">Nuevo Contacto</h4>
                    <form className="col s12" onSubmit={this.submitNuevaPersona.bind(this)}>
                        <div className="row">
                            <div className="input-field col s6">
                                <input id="nombre" ref="nombre" type="text" className="validate" />
                                <label htmlFor="nombre">Nombre: </label>
                            </div>
                            <div className="input-field col s6">
                                <input id="apellido" ref="apellido" type="text" className="validate" />
                                <label htmlFor="apellido">Apellido: </label>
                            </div>
                        </div>
                        <div className="row">
                            <div className="input-field col s6">
                                <input id="email" ref="email" type="email" className="validate" />
                                <label htmlFor="email">Email: </label>
                            </div>
                            <div className="input-field col s6">
                                <input id="telefono" ref="telefono" type="number" className="validate" />
                                <label htmlFor="telefono">Telefono: </label>
                            </div>
                            <div className="input-field col s6">
                                <input id="empresa" ref="empresa" type="text" className="validate" />
                                <label htmlFor="empresa">Empresa: </label>
                            </div>
                            
                        </div>
                        <div>
                            <button type="submit" name="action" className="waves-effect waves-light btn">Enviar</button>
                        </div>
                    </form>
                </div>
                </div>
                </div>
            </div>
        )
    }
   
}

export default PersonasForm;
